import React, {Component} from "react";
import {Link, Route, Switch} from "react-router-dom";
import {Layout, Menu} from 'antd';
import {PieChartOutlined, UserOutlined,} from '@ant-design/icons';

import routers from "../Router";

const {Header, Content, Footer, Sider} = Layout;
const {SubMenu} = Menu;

class Layouts extends Component {
    state = {
        collapsed: false,
    };

    onCollapse = collapsed => {
        console.log(collapsed);
        this.setState({collapsed});
    };

    render() {
        const {collapsed} = this.state;
        return (
            <Layout style={{minHeight: '100vh'}}>
                <Sider collapsible collapsed={collapsed} onCollapse={this.onCollapse}>
                    <div className="logo"/>
                    <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
                        {
                            routers.map(menu => (
                                    !menu.children ? (
                                        <Menu.Item key="1" icon={<PieChartOutlined/>}>
                                            <Link to={menu.path}>{menu.name}</Link>
                                        </Menu.Item>
                                    ) : (
                                        <SubMenu key={menu.path} icon={<UserOutlined/>} title={menu.name}>
                                            {menu.children.map(subMenu => (
                                                    <Menu.Item key={subMenu.path}>
                                                        <Link to={subMenu.path}>{subMenu.name}</Link>
                                                    </Menu.Item>
                                                )
                                            )}
                                        </SubMenu>
                                    )
                                )
                            )
                        }
                    </Menu>

                </Sider>
                <Layout className="site-layout">
                    <Header className="site-layout-background" style={{padding: 0}}/>
                    <Content style={{margin: '0 16px'}}>
                        <div className="site-layout-background" style={{padding: 24, minHeight: 360}}>
                            <Switch>
                                {
                                    routers.map(menu => {
                                        return !menu.children ? (
                                            <Route path={menu.path}>{menu.component}</Route>
                                        ) : (
                                            menu.children.map(subMenu => (
                                                <Route path={subMenu.path}> {subMenu.component}</Route>)
                                            )
                                        )
                                    })
                                }
                            </Switch>
                        </div>
                    </Content>
                    <Footer style={{textAlign: 'center'}}>Ant Design Â©2018 Created by HuyNX</Footer>
                </Layout>
            </Layout>
        );
    }
}


export default Layouts;
